sys_variable_value.value:
(function execute(inputs, outputs) {

  var accounts = JSON.parse(inputs.command.input).accounts

  cmd = 'cd ' + gs.getProperty('x_snc_hconnector.hcli_home') + ' && node hcli account balances '


  for (i=0; i < accounts.length; i++) {
    cmd = cmd + ' --accountid ' + accounts[i]

}

  outputs.cmd = cmd

})(inputs, outputs);

