sys_variable_value.value:
(function execute(inputs, outputs) {

  ecc = new ECCCommand()
  outputs.stdout = ecc.runAsync(gs.getProperty('x_snc_hconnector.midserver'), inputs.cmd, inputs.command.getTableName()+ ":" + inputs.command.sys_id)

})(inputs, outputs);

