sys_variable_value.value:
(function execute(inputs, outputs) {

     try {

       var cmdout = JSON.parse(inputs.cmdout);

       outputs.tx_id = cmdout.resp.transactionId;
       outputs.tx_hash = cmdout.resp.transactionHash;
       outputs.node_id = cmdout.resp.nodeId;

       outputs.tx_status = cmdout.receipt.status;
       outputs.account_id = cmdout.receipt.accountId;

       outputs.timestamp = cmdout.record.consensusTimestamp
       outputs.tx_fee = cmdout.record.transactionFee

       outputs.result = "OK";

    } catch (e) {
        outputs.result = "KO";
    }

})(inputs, outputs);

