sys_variable_value.value:
(function execute(inputs, outputs) {

var cmd = './hcli account create'

if (inputs.key) { cmd = cmd +  ' --key ' + inputs.key.getValue("public_key") } 
if (inputs.memo) { cmd = cmd +  ' --memo ' + inputs.memo } 
if (inputs.receiver_signature_required) { cmd = cmd + ' --reqsig' }

if (inputs.sign_with) {
  while (inputs.sign_with.next()) {
    cmd = cmd + ' --sign ' + inputs.sign_with.getValue("public_key")
  }   
}

outputs.cmd = cmd

})(inputs, outputs);

