sys_variable_value.value:
(function execute(inputs, outputs) {

var cmd = './hcli topic query '

if (inputs.topic) { cmd = cmd + ' --topicid ' + inputs.topic.getValue("topic_id") }
if (inputs.begin) { cmd = cmd + ' --start ' + inputs.start }
if (inputs.end) { cmd = cmd + ' --end ' +inputs.end }

outputs.cmd = cmd

})(inputs, outputs);

