sys_script_vtable.script:
(function executeQuery(v_table, v_query) {

    gr = new GlideRecord('x_snc_hconnector_account')
    gr.query()
    while (gr.next()) {

        (function() {

            try {
                var inputs = {};
                inputs['account_id'] = gr.account_id; // String 

                var result = sn_fd.FlowAPI.getRunner().action('x_snc_hconnector.queryaccountbalance2').inForeground().withInputs(inputs).run();
                var outputs = result.getOutputs();

                var out = outputs['out'];
				//gs.addInfoMessage(out)
                var res = JSON.parse(out);

                for (i = 0; i < res.resp.tokens.length; i++) {

                    var t = new GlideRecord('x_snc_hconnector_token');

                    if (t.get('token_id', res.resp.tokens[i].tokenId)) {
                        row = {
                            account: gr.sys_id,
                            account_id: gr.account_id,
                            name: gr.name,
                            token: t.sys_id,
                            token_id: res.resp.tokens[i].tokenId,
                            balance: res.resp.tokens[i].balance,
							decimals: res.resp.tokens[i].decimals,
							fmt_balance: new Utils().add_comma(res.resp.tokens[i].balance, Number(res.resp.tokens[i].decimals))
                        }

                        v_table.addRow(row)
                    }


                }

            } catch (ex) {
                var message = ex.getMessage();
                gs.error(message);
            }

        })();
    }


})(v_table, v_query);
